import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';



class CounterApp extends Component {
  // constructor(props){
  //   super(props);  

    state = {
      counter: 0
    // };

    // this.handleIncrement = this.handleIncrement.bind(this);
    // this.handleDecrement = this.handleDecrement.bind(this);
    // this.handleReset = this.handleReset.bind(this);
  }


  handleIncrement(){
    this.setState({counter:this.state.counter+1});
  }

  handleDecrement(){
    this.setState({counter:this.state.counter-1});
  }
  handleReset(){
    this.setState({counter:0});
  }

  render() {
    return (
      <div >
        <div>
          <h1 class="text-muted">{this.state.counter}</h1>
        </div>
        <div>
          <button onClick={this.handleIncrement.bind(this)} class= "btn btn-success"> + </button>
          <button onClick={this.handleReset.bind(this) } class= "btn btn-warning"> reset </button>
          <button onClick={this.handleDecrement.bind(this)} class= "btn btn-danger"> - </button>
        </div>
      </div>
    );
  }
}

export default CounterApp;
